-- -----------------------------------------------------
-- View `DBSSIIL`.`Orcom_Resolved2`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `DBSSIIL`.`Orcom_Resolved2`;
USE `DBSSIIL`;
CREATE  OR REPLACE VIEW `Orcom_Resolved2` AS
SELECT 
	Orcom.BD,
    Orcom.estado,
    Orcom.serie,
    Orcom.folio,
    Orcom.compBD,
    Orcom.compNumber,
    Companies.name as compName,
    Orcom.fhAutho,
    Orcom.fhETA,
    Orcom.fhArribo,
    Orcom.fhSurtido,
    Orcom.fhFin,
    Orcom.ownerName,
    Orcom.ownerPerson,
    Orcom.fhFolio,
    Orcom.suc,
    Orcom.sa,
    Orcom.fhEdit,
    Orcom.creator,
    Orcom.ID,
    Orcom.fhETAfl,
    Orcom.department
FROM Orcom 
JOIN Companies ON Orcom.compNumber = Companies.number;

